<style lang="scss" scoped>
.reportsDecoding {
  width: 100%;
  padding-bottom: 72px;
  &__wrapper {
    width: 100%;
    display: flex;
    justify-content: start;
    align-items: start;
    flex-direction: column;

    h1 {
      max-width: 525px;
      margin-bottom: 1rem;
      color: var(--primary-black);
    }
    p {
      max-width: 525px;
      margin-bottom: 2rem;
      color: var(--secondary-text-4);
    }
    &-images {
      width: 100%;
      display: flex;
      justify-content: start;
      align-items: center;
      flex-direction: row;
      flex-wrap: wrap;
      gap: 20px;
      @media screen and (max-width: 750px) {
        flex-direction: column;
        align-items: center;
      }
      div {
        width: calc(100% / 3 - 20px);
        border-radius: 56px;
        box-shadow: var(--box-shadow-black);
        overflow: hidden;
        min-width: 220px;
